INSERT INTO `yx_action` VALUES (2173, 2174, 0000, 0068, 0, '晓来百念都灰尽，剩有离人影。一钩残月向西流，对此不抛眼泪也无由。哎...');
INSERT INTO `yx_action` VALUES (2174, 2175, 0000, 0068, 0, '姑娘何事如此感慨？ 623');
INSERT INTO `yx_action` VALUES (2175, 2176, 0000, 0068, 0, '姑娘在此呤诗，我还是不打扰的好。 0');
INSERT INTO `yx_action` VALUES (2176, 0000, 0000, 0069, 0, '213');
INSERT INTO `yx_action` VALUES (2177, 2178, 0000, 0068, 0, '我的恋人常枫日前受人之托去了雪城附近，我很是挂念，但家有年迈祖母要照顾，故不能去寻找他，唉...');
INSERT INTO `yx_action` VALUES (2178, 2179, 0000, 0068, 0, '我有什么可以帮助你呢？ 624');
INSERT INTO `yx_action` VALUES (2179, 2176, 0000, 0068, 0, '哎，真可惜。 0');
INSERT INTO `yx_action` VALUES (2180, 2181, 0000, 0068, 0, '我亲手织了件衣服，但苦于路途艰远，看阁下的样子象是武林中人，能否帮我把衣服带给他呢？');
INSERT INTO `yx_action` VALUES (2181, 2182, 0000, 0068, 0, '助人乃快乐之本，在下一定尽力帮助姑娘完成心愿。 625');
INSERT INTO `yx_action` VALUES (2182, 2176, 0000, 0068, 0, '我还有事在身，不好意思呀。 0');
INSERT INTO `yx_action` VALUES (2183, 2184, 0000, 0068, 0, '谢谢你啊，据我打听，他有时候会到雪城旁边一个冰结成的洞窟那里的，好象是一个叫玉洁桥的地方。');
INSERT INTO `yx_action` VALUES (2184, 2176, 0000, 0068, 0, '姑娘放心，我一定会交给他。 626');
INSERT INTO `yx_action` VALUES (2185, 0000, 0000, 0001, 1069, '');
INSERT INTO `yx_action` VALUES (2186, 2187, 0000, 0068, 0, '你还没有找到他吗？他在雪城附近吧。');
INSERT INTO `yx_action` VALUES (2187, 2176, 0000, 0068, 0, '我马上就去了。 0');
INSERT INTO `yx_action` VALUES (2188, 2189, 0000, 0068, 0, '谢谢你，我儿时无意遇上一位象仙子般漂亮的大姐姐赠给我一条项链，据说可以守护佩戴之人的恋情，送给你做纪念吧。');
INSERT INTO `yx_action` VALUES (2189, 2176, 0000, 0068, 0, '谢谢了，也祝你们早日团聚。 629');
INSERT INTO `yx_action` VALUES (2190, 2191, 0000, 0002, 0, '雪山人参 ※常枫');
INSERT INTO `yx_action` VALUES (2191, 0000, 0000, 0001, 1071, '');
INSERT INTO `yx_action` VALUES (2214, 2215, 0000, 0068, 0, '只要带着恋之守护，你的爱情就会得到护佑而一帆风顺的，祝你和心爱的人幸福美满。');
INSERT INTO `yx_action` VALUES (2215, 2176, 0000, 0068, 0, '谢谢，也祝你们幸福。 0');
INSERT INTO `yx_action` VALUES (2192, 2193, 0000, 0068, 0, '你还没有去找可儿吗？');
INSERT INTO `yx_action` VALUES (2193, 2194, 0000, 0068, 0, '我马上就去了。 0');
INSERT INTO `yx_action` VALUES (2194, 0000, 0000, 0069, 0, '003');
INSERT INTO `yx_action` VALUES (2195, 2196, 0000, 0068, 0, '是可儿托你来的呀，真谢谢你了。哎，离开她这么久了，很想回去见她，可是我还有事暂时无法离开。有件事情想在烦劳阁下，不知可否？');
