INSERT INTO `yx_action` VALUES (2197, 2194, 0000, 0068, 0, '不好意思呀，我还有事在身，不太方便呀。 0');
INSERT INTO `yx_action` VALUES (2210, 2211, 0000, 0068, 0, '日前我在雪山采药时候发现一支雪山人参，对可儿祖母的身体大有益处，你能不能帮我带给可儿呢？');
