INSERT INTO `yx_action` VALUES (15139, 15140, 0000, 0068, 0, '我见过你父亲了，他很挂念你呀！ 10022');
INSERT INTO `yx_action` VALUES (15136, 15137, 0000, 0068, 0, '宫里的生活真的很无趣呀……陪我说说话好吗？以前我闷时，都是父亲与哥哥陪我聊天给我讲故事，好想念他们呀……');
INSERT INTO `yx_action` VALUES (15131, 15132, 0000, 0068, 0, '这是当时劫持我的人身上得到的令牌，听他的声音应该是当年与爹爹同朝为官的周仁。在我获救之后，他好象是向着山城方向跑了。');
INSERT INTO `yx_action` VALUES (15126, 15127, 0000, 0068, 0, '你父亲也很牵挂你们呀！你哥哥也应该没什么吧。 10017');
INSERT INTO `yx_action` VALUES (15164, 15105, 0000, 0068, 0, '官？……冤冤相报何时了，还是算了吧。 0');
INSERT INTO `yx_action` VALUES (15116, 15117, 0000, 0068, 0, '放心吧，我会尽力的！ 10014');
INSERT INTO `yx_action` VALUES (15110, 15111, 0000, 0068, 0, '他当时所去的方向……好象是山城方向，请大侠一定要帮我呀！');
