INSERT INTO `yx_action` VALUES (15100, 15101, 0000, 0068, 0, '好的！包在我身上了！ 0');
INSERT INTO `yx_action` VALUES (15501, 15096, 0000, 0068, 0, '呵呵，我正在努力呢。 0');
INSERT INTO `yx_action` VALUES (15093, 15094, 0000, 0068, 0, '想我一生忠君爱国、一心向善，想不到老来隐居至此，还遇上这般祸事，唉……');
