INSERT INTO `yx_action` VALUES (10965, 0000, 0000, 0001, 1521, '');
INSERT INTO `yx_action` VALUES (10954, 10955, 0000, 0068, 0, '请道长帮忙炼回天灵丹。 9258');
INSERT INTO `yx_action` VALUES (10937, 10938, 0000, 0068, 0, '这是我们的定情戒指，您带上给他，另外，帮我告诉他，我很快就可以与他团聚了。');
INSERT INTO `yx_action` VALUES (10950, 10951, 0000, 0068, 0, '您带着这颗内丹，到武当炼丹房找到元元真人，拜托他将此内丹炼制成回天灵丹好吗？');
INSERT INTO `yx_action` VALUES (10927, 10928, 0000, 0068, 0, '不关我事，这个东西我是捡的。 0');
INSERT INTO `yx_action` VALUES (10944, 10907, 0000, 0068, 0, '少庄主太客气了，谢了。 9265');
INSERT INTO `yx_action` VALUES (10923, 10924, 0000, 0068, 0, '请阁下转告她：无论她怎么样，我都会好好爱惜她的，希望她早日回家。拜托阁下了。');
INSERT INTO `yx_action` VALUES (10966, 10919, 0000, 0068, 0, '我也不想她离开我，但是她坚持要走...阁下如此高人，能否帮我找到我娘子？');
INSERT INTO `yx_action` VALUES (10915, 10907, 0000, 0068, 0, '那你娘子为何不见了？ 9249');
INSERT INTO `yx_action` VALUES (10912, 10967, 0000, 0068, 0, '后来那妖怪找上门，我才知娘子是蝴蝶修炼成人，那妖怪垂涎娘子内丹可增加自己的修行。');
INSERT INTO `yx_action` VALUES (10908, 10909, 0000, 0068, 0, '阁下真是高人，居然能将那妖怪降伏，那妖怪好厉害。娘子若非为了我，怎么会受伤远走。');
INSERT INTO `yx_action` VALUES (10892, 10893, 0000, 0068, 0, '你打败我，连我的内丹都拿走了，还想如何？');
