INSERT INTO `yx_action` VALUES (10871, 10872, 0000, 0068, 0, '是呀。 9233');
INSERT INTO `yx_action` VALUES (10870, 10871, 0000, 0068, 0, '阁下是否是我家老爷请来降妖的吗？');
INSERT INTO `yx_action` VALUES (10904, 10853, 0000, 0068, 0, '哦，好的。 0');
INSERT INTO `yx_action` VALUES (10903, 10904, 0000, 0068, 0, '我儿子在左厢房，可能他知道一些情况，阁下不妨找他问问。');
INSERT INTO `yx_action` VALUES (10902, 10853, 0000, 0068, 0, '茫茫人海，这个忙我帮不上了。 0');
INSERT INTO `yx_action` VALUES (10901, 10902, 0000, 0068, 0, '好吧。 9243');
INSERT INTO `yx_action` VALUES (10900, 10901, 0000, 0068, 0, '自从我媳妇失踪以后，小儿就郁郁寡欢，我想媳妇失踪与妖怪一定有所关系，您说妖怪还想找我媳妇，说明她没事，希望阁下能帮忙找她回来。');
INSERT INTO `yx_action` VALUES (10899, 10853, 0000, 0068, 0, '不好意思，我还有事呢。 0');
INSERT INTO `yx_action` VALUES (10898, 10899, 0000, 0068, 0, '什么事情？说来听听。 9242');
INSERT INTO `yx_action` VALUES (10897, 10898, 0000, 0068, 0, '真是太感谢了，这样我们全家老小就不用提心吊胆地过日子了。阁下如此厉害，是否能再帮我个忙呢？');
INSERT INTO `yx_action` VALUES (10869, 10853, 0000, 0068, 0, '好的。 0');
INSERT INTO `yx_action` VALUES (10868, 10869, 0000, 0068, 0, '阁下酉时再来找来福吧，一切拜托了。');
