INSERT INTO `yx_action` VALUES (10866, 10853, 0000, 0068, 0, '好的。 9230');
INSERT INTO `yx_action` VALUES (10865, 10866, 0000, 0068, 0, '我这里有把祖传宝剑，有驱邪的功效，阁下带上吧。');
INSERT INTO `yx_action` VALUES (10864, 10865, 0000, 0068, 0, '那你在晚上酉时之后再到我庄中正院的水井旁，我让家人来福指引你下井降妖。');
INSERT INTO `yx_action` VALUES (10863, 10853, 0000, 0068, 0, '这个...不好意思，我还有事，告辞。 0');
INSERT INTO `yx_action` VALUES (10862, 10863, 0000, 0068, 0, '放心吧，我一定可以降伏它。 9229');
INSERT INTO `yx_action` VALUES (10861, 10862, 0000, 0068, 0, '阁下可要慎重，井内的妖怪道行不浅，不少高人都受了伤呀。如果阁下真能将此妖怪降伏，那我全家老小真是太感激你了。');
INSERT INTO `yx_action` VALUES (10860, 10853, 0000, 0068, 0, '那你去请些世外高人帮忙吧，我先走了。 0');
INSERT INTO `yx_action` VALUES (10859, 10860, 0000, 0068, 0, '真有妖怪？我去降它。 9228');
INSERT INTO `yx_action` VALUES (10858, 10859, 0000, 0068, 0, '庄中水井不断地在夜里冒出红光，我怕是妖怪作祟就请了不少道士前来，结果都降服不了那个妖怪。愁得我...哎...');
INSERT INTO `yx_action` VALUES (10857, 10853, 0000, 0068, 0, '庄主无须叹气，事情总有解决的办法。 0');
INSERT INTO `yx_action` VALUES (10856, 10857, 0000, 0068, 0, '说说吧，也许我能帮忙。 9227');
INSERT INTO `yx_action` VALUES (10855, 10856, 0000, 0068, 0, '庄里出现这等怪事，真不知道怎么办才好。');
INSERT INTO `yx_action` VALUES (10854, 10855, 0000, 0068, 0, '哎，一言难尽呀，我们庄里有麻烦了。儿子莫名其妙病这么久，这才有一点好转，媳妇却失踪了...');
INSERT INTO `yx_action` VALUES (10853, 0000, 0000, 0069, 0, '390 0');
INSERT INTO `yx_action` VALUES (10947, 10853, 0000, 0068, 0, '那我就不打扰了。 0');
