INSERT INTO `yx_action` VALUES (11242, 11243, 0000, 0068, 0, '我请你提炼冰晶！ 5071');
INSERT INTO `yx_action` VALUES (11243, 11244, 0000, 0068, 0, '你为何在此？ 5069');
INSERT INTO `yx_action` VALUES (11244, 2194, 0000, 0068, 0, '我来此观赏风景！ 0');
INSERT INTO `yx_action` VALUES (11245, 11246, 0000, 0068, 0, '一言难尽啊...虽然我想告诉你，但是现在时机未到。到了恰当的时候再说吧。');
INSERT INTO `yx_action` VALUES (11246, 2194, 0000, 0068, 0, '哦，那不打扰了。 0');
INSERT INTO `yx_action` VALUES (11247, 11248, 0000, 0068, 0, '呵呵，你找对地方也找对人了。打败这里的怪物就有可能得到他们所收藏的冰石。');
INSERT INTO `yx_action` VALUES (11248, 11249, 0000, 0068, 0, '每六块冰石可以提炼出一颗冰晶，如果你得到足够的冰石，可以来找我，我帮你提炼成冰晶。');
INSERT INTO `yx_action` VALUES (11249, 2194, 0000, 0068, 0, '那我就先谢谢了。 0');
INSERT INTO `yx_action` VALUES (11250, 11251, 0000, 0068, 0, '提炼冰晶需要最少六块冰石，如果你身上带的冰石数量不够，不但我提炼一半白费工夫，还会浪费你辛苦找来的冰石。');
INSERT INTO `yx_action` VALUES (11251, 11252, 0000, 0068, 0, '你准备了足够的数量的冰石了吗？如果准备好了，我们就可以开始了。');
INSERT INTO `yx_action` VALUES (11252, 11253, 0000, 0068, 0, '我都准备好了。 5072');
INSERT INTO `yx_action` VALUES (11253, 2194, 0000, 0068, 0, '我准备好了再来找你吧。 0');
INSERT INTO `yx_action` VALUES (11254, 11255, 0000, 0068, 0, '你确定已经准备好了六块冰石了吗？由于提炼过程中如果数量不够就会白费，所以不要怪我罗嗦，要准备好了才开始呀！');
