INSERT INTO `yx_action` VALUES (11860, 0000, 0000, 0087, 0, 'QQ群953167432太古幻灵');
INSERT INTO `yx_action` VALUES (11865, 11866, 0000, 0068, 0, '原来你见过我两个姐姐了呀，她们还好我就放心了。其实过了这么久，我已经不为失去白玉扇而不开心了，而姐姐还这么记挂着我的事情...');
INSERT INTO `yx_action` VALUES (11866, 11867, 0000, 0068, 0, '我这里有一枚碧玉扳指，是我以前打败了一个敌人得到的。我认为最适合碧云姐姐佩带了，你帮我带给她，顺便帮我向姐姐们问好，好吗？');
INSERT INTO `yx_action` VALUES (11867, 11868, 0000, 0068, 0, '没问题。 5295');
