INSERT INTO `yx_action` VALUES (11870, 11871, 0000, 0002, 0, '白玉扇 ※柳随风');
INSERT INTO `yx_action` VALUES (11871, 11872, 0000, 0002, 0, '※碧星耳环 ※碧云');
INSERT INTO `yx_action` VALUES (11872, 0000, 0000, 0001, 1642, '');
INSERT INTO `yx_action` VALUES (11875, 11876, 0000, 0068, 0, '怎么？你还有事情？记得要帮我把扳指带给碧云姐姐呀。');
INSERT INTO `yx_action` VALUES (11876, 11853, 0000, 0068, 0, '好的。 0');
INSERT INTO `yx_action` VALUES (11955, 11956, 0000, 0068, 0, '月夜星空下是我一个人的舞台，独舞月下，多惬意呀！');
INSERT INTO `yx_action` VALUES (11956, 11957, 0000, 0068, 0, '你找我有什么事情呢？说是我以前的手下吗？恩...我好象没有收过小弟呀。我想想，好象有点印象...');
INSERT INTO `yx_action` VALUES (11957, 11958, 0000, 0068, 0, '请仔细想想吧。 5375');
INSERT INTO `yx_action` VALUES (11958, 11853, 0000, 0068, 0, '找错人了？那我告辞了。 0');
INSERT INTO `yx_action` VALUES (11960, 11961, 0000, 0068, 0, '哦，对了！好象以前魅影姐姐身边是有过一批人，但是他们怎么沦落到当强盗呢？真不象话，要做也要做一些比较有前途、有意义的职业呀。');
INSERT INTO `yx_action` VALUES (11961, 11962, 0000, 0068, 0, '不过不管怎么说，都曾经是姐姐的人，我可不能让他们坏了姐姐的名头呀。');
INSERT INTO `yx_action` VALUES (11962, 11853, 0000, 0068, 0, '原来是这样，希望你劝说他们改正。 5380');
INSERT INTO `yx_action` VALUES (11965, 11966, 0000, 0068, 0, '他们曾经是姐姐的手下，我可管不了他们的事情！这件事情可能要姐姐才能处理。不过姐姐比较难讲话，我怕你独自跑去找她，她都不理你。');
INSERT INTO `yx_action` VALUES (11966, 11967, 0000, 0068, 0, '姐姐性格我还是很了解的，她可不会管这种闲事，除非...');
INSERT INTO `yx_action` VALUES (11967, 11968, 0000, 0068, 0, '除非如何？ 5385');
INSERT INTO `yx_action` VALUES (11968, 11853, 0000, 0068, 0, '啊，那我不管此事了。 0');
INSERT INTO `yx_action` VALUES (11970, 11971, 0000, 0068, 0, '我以前丢失了一把白玉扇，是一个叫柳随风的小偷盗走的。据说他后来还被当成英雄埋到一个隐秘的地方，如果你帮我找回来，我就帮你。');
INSERT INTO `yx_action` VALUES (11971, 11972, 0000, 0068, 0, '那好吧。 0');
INSERT INTO `yx_action` VALUES (11972, 11853, 0000, 0068, 0, '不是吧，这么麻烦？ 0');
INSERT INTO `yx_action` VALUES (11975, 11976, 0000, 0068, 0, '总算能见到我的白玉扇了！既然你帮我把白玉扇找回来了，我也决不食言。这是我的黄玉戒指，你带上它去找姐姐，姐姐一定会帮忙的。');
INSERT INTO `yx_action` VALUES (11976, 11853, 0000, 0068, 0, '谢谢。 5395');
