INSERT INTO `yx_action` VALUES (11990, 11991, 0000, 0068, 0, '怎么？你还不去找魅影姐姐，是有事情吗？');
INSERT INTO `yx_action` VALUES (11991, 11853, 0000, 0068, 0, '马上就去。 0');
INSERT INTO `yx_action` VALUES (11925, 11926, 0000, 0068, 0, '怎么办？怎么办？这下可麻烦了。这样要我们大家怎么活呀？天呐，谁能帮我们大家呀。');
INSERT INTO `yx_action` VALUES (11926, 11927, 0000, 0068, 0, '此地出了什么事情？ 5345');
INSERT INTO `yx_action` VALUES (11927, 0000, 0000, 0069, 0, '310 0');
INSERT INTO `yx_action` VALUES (11930, 11931, 0000, 0068, 0, '阁下看起来气宇不凡，也许能帮上我们。我就把事情告诉你吧。本城地处沙漠中央，水源来源只有雨水或远处的青之绿洲。');
INSERT INTO `yx_action` VALUES (11931, 11932, 0000, 0068, 0, '最近绿洲被一伙沙漠巨盗把持，说我们要到那里取水就要交纳保护费。我们城中百姓都无钱交纳，很多年轻精壮的人前去讨伐沙盗，都...');
INSERT INTO `yx_action` VALUES (11932, 11927, 0000, 0068, 0, '不用担心，我帮你们解决这些强盗。 5350');
INSERT INTO `yx_action` VALUES (11935, 11936, 0000, 0068, 0, '那伙沙漠巨盗据说曾经是火系终极幻灵兽紫衣璇紫的手下，听说他们除了听从璇紫的命令外，谁都不放在眼里，是一群出了名的悍匪呀！');
