INSERT INTO `yx_action` VALUES (600346, 600347, 0000, 0068, 0, '我有一块神铁，本来准备给大禹用来测量水深，却不小心被我身边的孽畜黑龙使给偷走了，它就凭此神铁在海里横行霸道，危害人类。');
