INSERT INTO `yx_action` VALUES (600127, 600133, 0000, 0068, 0, '七年以后，我已经达到从心所念而无是非对错，随口所言而无利害得失，老师才准我和他并席而坐。');
INSERT INTO `yx_action` VALUES (600131, 600123, 0000, 0068, 0, '哦，后来呢？ 600052');
INSERT INTO `yx_action` VALUES (600126, 600131, 0000, 0068, 0, '记得当年我跟从壶丘子学习时，经历了3年的磨练，变得心不敢有是非之念，口不敢言利害得失，才勉强赢得老师多看一眼而已。');
INSERT INTO `yx_action` VALUES (600130, 600126, 0000, 0068, 0, '你说御风而行啊，呵呵，那可不是一朝一夕就能学会的啊。');
