INSERT INTO `yx_action` VALUES (600120, 600121, 0000, 0068, 0, '天地之道，非阴则阳；圣人之教，非仁则义；万物之宜，非柔则刚。年轻人，你来此有何贵干啊？');
INSERT INTO `yx_action` VALUES (600125, 600123, 0000, 0068, 0, '好深奥哦。 0');
INSERT INTO `yx_action` VALUES (600132, 600127, 0000, 0068, 0, '五年之后，我又练成另一种心念是非、口言利害的心境，才勉强博得老师的会心一笑。');
INSERT INTO `yx_action` VALUES (600124, 600125, 0000, 0068, 0, '形动不生形而生影，声运不生声而生响，无动不生无而生有。');
