INSERT INTO `yx_action` VALUES (600297, 600298, 0000, 0068, 0, '可怜的是我从小青梅竹马一起长大的恋人艺玲，就这样在家乡苦苦等候了五年。');
INSERT INTO `yx_action` VALUES (600298, 600076, 0000, 0068, 0, '我一年半载才能回去看她一次，除此之外，我们只能靠书信来交流，艺玲却一直无怨无悔，想起她，我心中充满了思念和愧疚啊。');
INSERT INTO `yx_action` VALUES (600076, 600077, 0000, 0068, 0, '你不想为她做些什么吗？ 600032');
INSERT INTO `yx_action` VALUES (600077, 600296, 0000, 0068, 0, '真是一场苦恋！ 0');
INSERT INTO `yx_action` VALUES (600083, 600084, 0000, 0068, 0, '哎，今年恐怕又没有机会回去了，我还真不知道有什么可以为她做的，你能帮我想想吗？');
INSERT INTO `yx_action` VALUES (600084, 600087, 0000, 0068, 0, '给她写封情书？ 600033');
