INSERT INTO `yx_action` VALUES (600092, 600296, 0000, 0068, 0, '那就送份礼物吧。 600034');
INSERT INTO `yx_action` VALUES (600094, 600292, 0000, 0068, 0, '嗯，礼物我倒真的准备了很久，这盆名为“真爱的承诺”的玫瑰花是用当年我和艺玲一起寻得的“承诺的花种”栽成的，你帮我带给她好吗？');
INSERT INTO `yx_action` VALUES (600292, 600293, 0000, 0068, 0, '义不容辞！ 600035');
INSERT INTO `yx_action` VALUES (600293, 600296, 0000, 0068, 0, '还是你自己送吧，丢了我可赔不起。 0');
INSERT INTO `yx_action` VALUES (600294, 600289, 0000, 0001, 600005, '');
INSERT INTO `yx_action` VALUES (600289, 600279, 0000, 0068, 0, '真是太谢谢你了，艺玲是个爱花的女子，每天都会在四桥原种花赏花。');
INSERT INTO `yx_action` VALUES (600279, 600251, 0000, 0068, 0, '另外，麻烦你再帮我捎句话，要她好好保重好身体，等我回来，我会在远方天天为她祝福，并永远爱她！');
